import { createStore } from "vuex";

const store = createStore({
  state: {},
  getters: {},
  mutations: {},
  actions: {
      fetchAllPosts (store) {
        return fetch('/api/something')
          .then(data => {
            store.commit('setSomething', data.something)
            return store.state.something
          })
      }
    },
  modules: {},

  })

export default store;
