import { createStore } from "vuex";

const store = createStore({
  state: {
    something: "",
  },
  getters: {},

  mutations: {
    setSomething(state, something) {
      state.something = String(something);
    },
  },
  actions: {
    async fetchAllPosts(store) {
      var requestOptions = {
        method: "GET",
        headers: "myHeaders",
        redirect: "follow",
      };

      fetch("https://jsonplaceholder.typicode.com/posts", requestOptions).then(
        (data) => {
          store.commit("setSomething", data.something);
          return store.state.something;
        }
      );
    },
  },
  modules: {},
});

export default store;
