import { createStore } from "vuex";

const store = createStore({
  state: {
    something: "",
  },
  getters: {},

  mutations: {
    setSomething(state, payload) {
      state.something = payload;
    },
  },
  actions: {
    async fetchAllPosts() {
      var myHeaders = new Headers();
      myHeaders.append("Content-Type", "text/json");

      var requestOptions = {
        method: "GET",
        headers: myHeaders,
        // redirect: "follow",
      };

      fetch("https://jsonplaceholder.typicode.com/todos/", requestOptions)
        .then((response) => response.json())
        .then((json) => store.commit("setSomething", json));
      console.log(store.state.something);
      return store.state.something;
    },
    async fetchOnePost(idpost){
      var myHeaders = new Headers();
      myHeaders.append("Content-Type", "text/json");

      var requestOptions = {
        method: "GET",
        headers: myHeaders,
        // redirect: "follow",
      };
      console.log(idpost)
      fetch("https://jsonplaceholder.typicode.com/todos/"+idpost, requestOptions)
      .then((response) => response.json())
      .then((json) => store.commit("setSomething", json));
      console.log(store.state.something);
      return store.state.something;
    }
  },
  modules: {},
});

export default store;
