import { createStore } from "vuex";

const store = createStore({
  state: {
    something: "",
  },
  getters: {},

  mutations: {
    setSomething(state, payload) {
      state.something = payload;
    },
  },
  actions: {
    async fetchAllPosts() {
      var myHeaders = new Headers();
      myHeaders.append('Content-Type', 'text/json');

      var requestOptions = {
        method: "GET",
        headers: myHeaders,
        // redirect: "follow",
      };

      fetch("https://jsonplaceholder.typicode.com/todos/", requestOptions)
      .then(response => response.json())
      .then(json => store.commit("setSomething", response))
      // console.log(store.state.something)
        return store.state.something;
        }
      },
  modules: {},
});

export default store;
